      value: ev.target.value,
    });
  };
  const handleSubmit = (ev) => {
    if (ev.key === 'Enter') {
      ev.preventDefault();
    }
  };

  return (
    <form className="form__search" onKeyDown={handleSubmit}>
      <header className="header">
        <img
          className="header__image"
          src={logo}
          alt="Logo Rick and Morty"
          title="Logo Rick and Morty"
        />
