            className="select__input"
            name="filterSpecie"
            id="filterSpecie"
            value={props.filterSpecie}
            onChange={handleFilterNameChild}
          >
            <option value="all">Todos</option>
            <option value="Humano">Humano</option>
            <option value="Alien">Alien</option>
          </select>
        </div>
      </header>
